import React, { useState, useEffect } from "react";
import axios from "axios";
import FeatureBanner from "./components/home/FeatureBanner";

const App = () => {
  const [posts, setPosts] = useState([]);
  const [featuredProduct, setFeaturedProduct] = useState({});

  const id = "66230e93862e1249483e6c43";

  useEffect(() => {
    //fetch product data from express server
    axios
      .get(`http://localhost:5050/api/products/${id}`)
      .then((response) => {
        setFeaturedProduct(response.data);
        console.log(featuredProduct);
      })
      .catch((error) => {
        console.log("Error getting product data from server");
      });

    //fetch post data from express server
    axios
      .get(`http://localhost:5050/api/posts`)
      .then((response) => {
        setPosts(response.data);
        console.log(posts);
      })
      .catch((error) => {
        console.log("Error getting post data from server.");
      });
  }, []);

  return (
    <div>
      <FeatureBanner featuredProduct={featuredProduct} />

      <hr />

      <h2>Latest Posts</h2>
      {posts.map((post) => (
        <>
          <h3 key={post._id}>{post.title}</h3>
          <p>{post.content}</p>
          <p>Published On: {new Date(post.createdAt).toLocaleDateString()}</p>
        </>
      ))}
    </div>
  );
};

export default App;
