import React, { useState } from "react";
import { sendContactMessage } from "../../services/formService";

const Form = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    subject: "",
    message: "",
  });

  const sendEmail = (event) => {
    event.preventDefault();

    console.log("We will fill this up shortly.");
    // code to trigger Sending email
  };

  const onInputChange = (event) => {
    const { name, value } = event.target;

    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <div className="py-20 px-10 md:px-20 lg:px-40 ">
      <div className="p-6 bg-blue-200 content-center rounded-lg hover:shadow-2xl">
        <div className="grid grid-cols-1 sm:grid-cols-1 md:grid-cols-2">
          <div>
            <h2 className="text-2xl pb-2">Reach Out to Us</h2>
            <p>
              As the creators of I-Meditate, we are passionate about leveraging
              technology to bring people together in pursuit of well-being and
              inner harmony.
            </p>
          </div>

          <div>
            <form onSubmit={sendEmail}>
              <Form.Group controlId="name">
                <Form.Label>Full Name</Form.Label>
                <Form.Control
                  type="text"
                  name="name"
                  value={state.name}
                  placeholder="Enter your full name"
                  onChange={onInputChange}
                />
              </Form.Group>
              <Form.Group controlId="email">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="text"
                  name="email"
                  value={state.email}
                  placeholder="Enter your email"
                  onChange={onInputChange}
                />
              </Form.Group>
              <Form.Group controlId="subject">
                <Form.Label>Subject</Form.Label>
                <Form.Control
                  type="text"
                  name="subject"
                  value={state.subject}
                  placeholder="Enter subject"
                  onChange={onInputChange}
                />
              </Form.Group>
              <Form.Group controlId="subject">
                <Form.Label>Message</Form.Label>
                <Form.Control
                  as="textarea"
                  name="message"
                  value={state.message}
                  rows="3"
                  placeholder="Enter your message"
                  onChange={onInputChange}
                />
              </Form.Group>
              <button className="bg-blue-500" type="submit">
                Submit
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Form;
