import React from "react";

const PostContent = ({ post }) => {
  // Check if post and post.content exist before rendering
  if (!post || !post.content) {
    return null; // or handle the case where post or post.content is undefined
  }

  return (
    <div className="lg:col-span-7 py-20 px-10 xl:pl-20">
      {/* Introduction */}
      <div>
        <h2 className="text-2xl">{post.content.intro.heading}</h2>
        <p>{post.content.intro.text}</p>
      </div>

      {/* Sections */}
      {post.content.sections.map((section, index) => (
        <div key={index} className="my-8">
          <h2 className="text-2xl">{section.heading}</h2>
          <p>{section.text}</p>
          {section.image && <img src={section.image} alt="" className="my-4" />}
        </div>
      ))}

      {/* Conclusion */}
      <div>
        <h2 className="text-2xl">{post.content.conclusion.heading}</h2>
        <p>{post.content.conclusion.text}</p>
      </div>
    </div>
  );
};

export default PostContent;
