import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getFeatureProduct } from "../services/productService";

const Navbar = () => {
  const [id, setId] = useState("");
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    const getData = async () => {
      try {
        // this should be changed to accomodate the products
        // gallery page by linking to /products --> products
        // page compenent which links to /products:id
        const productData = await getFeatureProduct();
        setId(productData._id);
      } catch (error) {
        console.log("Error fetching data:", error);
      }
    };

    getData();
  }, []);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="m-2 relative">
      {/* Hamburger icon */}
      <button
        onClick={toggleMenu}
        className="block text-white focus:outline-none"
      >
        <svg className="h-8 w-8 hover:" fill="none" viewBox="0 0 24 24" stroke="white">
          {isOpen ? (
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M6 18L18 6M6 6l12 12"
            ></path>
          ) : (
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M4 6h16M4 12h16m-7 6h7"
            ></path>
          )}
        </svg>
      </button>

      {/* Dropdown menu */}
      <nav
        className={`font-semibold text-blue-500 bg-white mt-4 ${
          isOpen ? "block" : "hidden"
        }`}
      >
        <Link
          to="/"
          className="block px-4 py-2 hover:bg-blue-500 hover:text-white"
        >
          Home
        </Link>
        <Link
          to={`/products/${id}`}
          className="block px-4 py-2 hover:bg-blue-500 hover:text-white"
        >
          Products
        </Link>
        <Link
          to="/posts"
          className="block px-4 py-2 hover:bg-blue-500 hover:text-white"
        >
          Posts
        </Link>
        <Link
          to="/about"
          className="block px-4 py-2 hover:bg-blue-500 hover:text-white"
        >
          About
        </Link>
        <Link
          to="/contact"
          className="block px-4 py-2 hover:bg-blue-500 hover:text-white"
        >
          Contact
        </Link>
      </nav>
    </div>
  );
};

export default Navbar;
