import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getFeatureProduct } from "../services/productService";

const Navbar = () => {
  const [id, setId] = useState("");
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    const getData = async () => {
      try {
        // this should be changed to accomodate the products
        // gallery page by linking to /products --> products
        // page compenent which links to /products:id
        const productData = await getFeatureProduct();
        setId(productData._id);
      } catch (error) {
        console.log("Error fetching data:", error);
      }
    };

    getData();
  }, []);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="pt-6">
      {/* Hamburger icon to toggle the dropdown menu */}
      <button onClick={toggleMenu} className="block md:hidden text-white">
        <svg
          className="h-6 w-6"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            d="M4 6h16M4 12h16m-7 6h7"
          ></path>
        </svg>
      </button>

      {/* Dropdown menu */}
      <nav
        className={`font-semibold text-white gap-x-4 ${
          isOpen ? "block" : "hidden"
        }`}
      >
        <Link to="/" className="block md:inline-block">
          Home
        </Link>
        <Link to="/products" className="block md:inline-block">
          Products
        </Link>
        <Link to="/posts" className="block md:inline-block">
          Posts
        </Link>
        <Link to="/about" className="block md:inline-block">
          About
        </Link>
        <Link to="/contact" className="block md:inline-block">
          Contact
        </Link>
      </nav>
    </div>
  );
};

export default Navbar;
