import React from "react";
import { Link } from "react-router-dom";

const PostCard = ({ post }) => {
  return (
    <div>
      <Link to={"/"}>
        <div className="bg-blue-200 rounded-lg hover:shadow-2xl overflow-hidden min-h-full">
          <img
            src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQHq2GPqup7XzN0o8A9WAs4uGzfCESHbA50wm1WlGv_xw&s"
            alt="[FEATURE IMG]"
            className="w-full h-auto"
          ></img>
          <div className=" bg-blue-500 p-4 opacity-[80%]">
            <h2 className="text-2xl text-white font-semibold mb-2 ">
              {post.title}
            </h2>
            <div className="grid grid-cols-2 mt-6">
              <p className="text-md text-white mb-2 text-left">
                {post.category}
              </p>
              <p className="text-md text-white mb-2 text-right">
                Published: {new Date(post.createdAt).toLocaleDateString()}
              </p>
            </div>
          </div>
        </div>
      </Link>
    </div>
  );
};

export default PostCard;
