import React, { useState, useEffect } from "react";
import { getPosts } from "../services/postService";
import Banner from "../components/posts/Banner";
import Filters from "../components/posts/Filters";
import PostsCards from "../components/posts/PostsCards";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [filteredPosts, setFilteredPosts] = useState([]);
  const [filter, setFilter] = useState(null);

  useEffect(() => {
    const getData = async () => {
      try {
        const postData = await getPosts();
        setPosts(postData);
        setFilteredPosts(postData); // Initialize filtered posts with all posts
      } catch (error) {
        console.log("Error fetching data:", error);
      }
    };

    getData();
  }, []);

  useEffect(() => {
    // Apply filter
    if (filter) {
      const filtered = posts.filter((post) => post.filter === filter); // Change 'filter' to the property you're filtering by
      setFilteredPosts(filtered);
    } else {
      // Clear filter
      setFilteredPosts(posts);
    }
  }, [filter, posts]);

  return (
    <div className="">
      <Banner />
      <Filters filter={filter} setFilter={setFilter} />
      <PostsCards posts={filteredPosts} />
    </div>
  );
};

export default Posts;
