const Pagination = ({ currentPage, postsPerPage, totalPosts, paginate }) => {
  const pageNumbers = Math.ceil(totalPosts / postsPerPage);

  // Define the range of visible page numbers
  const maxPageNumbers = 5;
  const halfMaxPageNumbers = Math.floor(maxPageNumbers / 2);
  let startPage = Math.max(1, currentPage - halfMaxPageNumbers);
  let endPage = Math.min(startPage + maxPageNumbers - 1, pageNumbers);

  // Adjust startPage when near the end of the list to always show maxPageNumbers
  if (endPage - startPage + 1 < maxPageNumbers) {
      startPage = Math.max(1, endPage - maxPageNumbers + 1);
  }

  return (
      <div className="flex justify-center p-20">
          <button
              onClick={() => paginate(currentPage - 1)}
              disabled={currentPage === 1}
              className="px-4 py-2 mx-20 bg-blue-500 text-white rounded-lg shadow-md"
          >
              Previous
          </button>

          {/* Display the page numbers */}
          {Array.from({ length: endPage - startPage + 1 }, (_, i) => (
              <button
                  key={startPage + i}
                  onClick={() => paginate(startPage + i)}
                  className={`p-1 ${currentPage === startPage + i ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'} rounded-lg shadow-md mr-2`}
              >
                  {startPage + i}
              </button>
          ))}

          <button
              onClick={() => paginate(currentPage + 1)}
              disabled={currentPage === pageNumbers}
              className="px-4 py-2 mx-20 bg-blue-500 text-white rounded-lg shadow-md"
          >
              Next
          </button>
      </div>
  );
}

export default Pagination;
