import React, { useState, useEffect } from "react";
import { getPosts } from "../services/postService";
import Banner from "../components/posts/Banner";
import Filters from "../components/posts/Filters";
import PostsCards from "../components/posts/PostsCards";
import Pagination from "../components/posts/Pagination";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [filteredPosts, setFilteredPosts] = useState([]);
  const [filter, setFilter] = useState("");
  const [searchQuery, setSearchQuery] = useState("");

  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const postsPerPage = 4;

  useEffect(() => {
    fetchData();
  }, [currentPage, filter, searchQuery]); // Update when page, filter, or searchQuery changes

  const fetchData = async () => {
    try {
      const response = await getPosts({
        page: currentPage,
        pageSize: postsPerPage,
        category: filter,
        searchQuery: searchQuery
      });
      setFilteredPosts(response.posts);
      setTotalPages(response.totalPages);

    } catch (error) {
      console.log("Error fetching data:", error);
    }
  };

  const pageNumbers = [];
  for (let i = 1; i <= totalPages; i++) {
    pageNumbers.push(i);
  }

  const handleSearch = (query) => {
    setSearchQuery(query);
    setCurrentPage(1); // Reset current page when search query changes
  };

  const handleFilter = (selectedFilter) => {
    setFilter(selectedFilter);
    setCurrentPage(1); // Reset current page when filter changes
  };

  const handlePagination = (pageNumber) => {
    setCurrentPage(pageNumber);
  };
  // const [posts, setPosts] = useState([]);
  // const [filteredPosts, setFilteredPosts] = useState([]);
  // const [filter, setFilter] = useState("");
  // const [searchQuery, setSearchQuery] = useState("");


  // useEffect(() => {
  //   const fetchData = async () => {
  //     try {
  //       const postData = await getPosts();
  //       setPosts(postData);
  //       setFilteredPosts(postData); // Initialize filtered posts with all posts
  //     } catch (error) {
  //       console.log("Error fetching data:", error);
  //     }
  //   };

  //   fetchData();
  // }, []);

  // // Function to handle search
  // const handleSearch = (query) => {
  //   setSearchQuery(query); // Update searchQuery state
  //   const filtered = posts.filter(
  //     (post) =>
  //       post.title.toLowerCase().includes(query.toLowerCase()) ||
  //       post.author.toLowerCase().includes(query.toLowerCase()) ||
  //       post.category.toLowerCase().includes(query.toLowerCase()) ||
  //       post.content.toLowerCase().includes(query.toLowerCase())
  //   );
  //   setFilteredPosts(filtered); // Update filteredPosts state
  // };

  // const handleFilter = (filter) => {
  //   setFilter(filter);
  //   const filtered = posts.filter((post) =>
  //     post.category.toLowerCase().includes(filter.toLowerCase())
  //   );
  //   setFilteredPosts(filtered);
  // };

  return (
    <div className="">
      <Banner />
      <Filters handleFilter={handleFilter} handleSearch={handleSearch} />
      <PostsCards posts={filteredPosts} />

      {/* PAGINATION */}
      {/* <Pagination
        currentPage={currentPage}
        totalPages={totalPages}
        onPageChange={handlePagination}
      /> */}
       <div className="flex justify-center my-4">
      <nav>
        <ul className="pagination">
          <li className={`page-item ${currentPage === 1 && 'disabled'}`}>
            <button
              className="page-link"
              onClick={() => handlePagination(currentPage - 1)}
              disabled={currentPage === 1}
            >
              Previous
            </button>
          </li>
          {pageNumbers.map((number) => (
            <li
              key={number}
              className={`page-item ${currentPage === number && 'active'}`}
            >
              <button
                className="page-link"
                onClick={() => handlePagination(number)}
              >
                {number}
              </button>
            </li>
          ))}
          <li
            className={`page-item ${currentPage === totalPages && 'disabled'}`}
          >
            <button
              className="page-link"
              onClick={() => handlePagination(currentPage + 1)}
              disabled={currentPage === totalPages}
            >
              Next
            </button>
          </li>
        </ul>
      </nav>
    </div>
    </div>
  );
};

export default Posts;
