import React from "react";
import { Link } from "react-router-dom";

const PostBanner = ({ post }) => {
  // Check if post and post.content exist before rendering
  if (!post || !post.content) {
    return null; // or handle the case where post or post.content is undefined
  }

  // Style object for the background image
  const bannerStyle = {
    backgroundImage: `url(${post.featureImage})`,
    height: "500px", // Adjust height as needed
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
  };

  return (
    <div>
      <div className="bg-orange-300 text-white text-center" style={bannerStyle}>
        <div className="">
          <Link
            to={`/posts`}
            className="
              bg-orange-500  
              text-white
              px-4 
              py-2 
              rounded-lg 
              shadow-lg 
              hover:bg-blue-500 
              hover:text-white 
              transition 
              duration-300"
          >
            BACK
          </Link>
        </div>

        <div className="bg-orange-300 flex text-center items-center text-white">
        <div className="">
          <h2 className="text-5xl">{post.title}</h2>
          <h4 className="text-2xl">{post.subtitle}</h4>
        </div>

        <div className="text-center md:flex md:justify-evenly px-20 text-xl font-semibold">
          <p className="p-2">{post.category}</p>
          <p className="p-2">{post.author}</p>
          <p className="p-2">
            {new Date(post.createdAt).toLocaleDateString(0)}
          </p>
        </div>
        </div>


      </div>
    </div>
  );
};

export default PostBanner;
