import React from "react";
import { Link } from "react-router-dom";

const PostBanner = ({ post }) => {
  // Check if post and post.content exist before rendering
  if (!post || !post.content) {
    return null; // or handle the case where post or post.content is undefined
  }

  // Style object for the background image
  const bannerStyle = {
    backgroundImage: `url(${post.featureImage})`,
    height: "500px", // Adjust height as needed
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    position: "relative", // Necessary for overlay positioning
  };

  // Style object for the overlay
  const overlayStyle = {
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    backgroundColor: "rgba(0, 0, 0, 0.3)", // 30% black overlay
    zIndex: 1, // Ensure the overlay is above the background image
  };

  return (
    <div>
      <div className="relative bg-orange-300 text-white text-center" style={bannerStyle}>
        {/* Overlay */}
        <div style={overlayStyle}></div>

        {/* Content */}
        <div className="absolute top-0 left-0 z-10 px-4 py-6">
          <Link
            to={`/posts`}
            className="
              bg-orange-500  
              text-white
              px-4 
              py-2 
              rounded-lg 
              shadow-lg 
              hover:bg-orange-300 
              hover:text-orange-500 
              transition 
              duration-300"
          >
            BACK
          </Link>
        </div>

        <div className="absolute inset-0 flex flex-col justify-center items-center gap-10">
          <h2 className="text-5xl">{post.title}</h2>
          <h4 className="text-2xl">{post.subtitle}</h4>

          <div className="text-center md:flex md:justify-evenly px-20 gap-x-10 text-xl">
            <p className="p-2">{post.category}</p>
            <p className="p-2">{post.author}</p>
            <p className="p-2">
              {new Date(post.createdAt).toLocaleDateString(0)}
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PostBanner;
