import React, { useState } from "react";
import {
  isNotEmpty,
  validateEmail,
  messageHasLength,
  subjectHasLength
} from "../../services/formService";
import axios from "axios";

const Form = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    subject: "",
    message: "",
  });
  const [errors, setErrors] = useState({});
  const [isLoading, setIsLoading] = useState(false);

  const validate = () => {
    let temp = {};

    temp.name = isNotEmpty(form.name) ? "" : "Name is Required";
    temp.email = validateEmail(form.email) ? "" : "Incorrect format email";
    temp.message = subjectHasLength(form.subject)
    ? ""
    : "Subject is required or too short";
    temp.message = messageHasLength(form.message)
      ? ""
      : "Message is required or too short";

    setErrors({ ...temp });

    return Object.values(temp).every((value) => value === "");
  };

  const handleOnChange = (event) => {
    const field = event.target.name;
    setForm({ ...form, [field]: event.target.value });

    if ((field === "name") | (field === "city"))
      setErrors({
        ...errors,
        [field]: isNotEmpty(event.target.value) ? "" : "Name is Required",
      });
    else if (field === "email")
      setErrors({
        ...errors,
        [field]: validateEmail(event.target.value)
          ? ""
          : "Incorrect format email",
      });
      else if (field === "subject")
      setErrors({
        ...errors,
        [field]: subjectHasLength(event.target.value)
          ? ""
          : "Subject is required or too short",
      });
    else if (field === "message")
      setErrors({
        ...errors,
        [field]: messageHasLength(event.target.value)
          ? ""
          : "Message is required or too short",
      });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setIsLoading(true);
    if (validate()) {
      try {
        const response = await axios.post("/api/contact", form);
        if (response.status === 200) {
          window.alert(
            `${response.data.message}, check console for the response`
          );
          setIsLoading(false);
          setForm({
            fname: "",
            email: "",
            telephone: "",
            city: "",
            message: "",
          });
        }
        console.log(response);
      } catch (error) {
        window.alert(`${error}, check console for the error`);
        console.log(error);
        setIsLoading(false);
      }
    } else setIsLoading(false);
  };

  return (
    <div className="py-20 px-10 md:px-20 lg:px-40 ">
      <div className="p-6 bg-blue-200 content-center rounded-lg hover:shadow-2xl">
        <div className="grid grid-cols-1 sm:grid-cols-1 md:grid-cols-2">
          <div>
            <h2 className="text-2xl pb-2">Reach Out to Us</h2>
            <p>
              As the creators of I-Meditate, we are passionate about leveraging
              technology to bring people together in pursuit of well-being and
              inner harmony.
            </p>
          </div>

          <div>
            <form action="" className="">
              <label htmlFor="">Name</label>
              <input type="text" className="w-full" />
              <label htmlFor="">Subject</label>
              <input type="text" className="w-full" />
              <label htmlFor="">Message</label>
              <input type="text" className="w-full" />
              <button type="submit">SEND MESSAGE</button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Form;
