import React from "react";

const Pagination = ({ currentPage, postsPerPage, length, handlePagination }) => {
  const paginationNumbers = [];

  for (let i = 1; i <= Math.ceil(length / postsPerPage); i++) {
    paginationNumbers.push(i);
  }

  return (
    <div className="flex items-center justify-around mt-4">
    {/* Previous Page Arrow */}
    <button
      className={`pagination-arrow ${currentPage === 1 ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-200'}`}
      onClick={() => handlePagination(currentPage - 1)}
      disabled={currentPage === 1}
    >
      {"<"}
    </button>
    <div>
    {paginationNumbers.map((pageNumber) => (
      <button
        key={pageNumber}
        className={`mx-1 py-1 px-3 rounded-full ${currentPage === pageNumber ? 'bg-blue-500 text-white' : 'hover:bg-gray-200'}`}
        onClick={() => handlePagination(pageNumber)}
      >
        {pageNumber}
      </button>
    ))}
    </div>
    {/* Page Numbers */}

    {/* Next Page Arrow */}
    <button
      className={`pagination-arrow ${currentPage === Math.ceil(length / postsPerPage) ? 'opacity-50 cursor-not-allowed' : 'hover:bg-gray-200'}`}
      onClick={() => handlePagination(currentPage + 1)}
      disabled={currentPage === Math.ceil(length / postsPerPage)}
    >
      {">"}
    </button>
  </div>
  );
};

export default Pagination;
