import React, { useState, useEffect } from "react";
import { getPosts } from "../services/postService";
import Banner from "../components/posts/Banner";
import Filters from "../components/posts/Filters";
import PostsCards from "../components/posts/PostsCards";
import Pagination from "../components/posts/Pagination";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [filteredPosts, setFilteredPosts] = useState([]);
  const [filter, setFilter] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [currentPage, setCurrentPage] = useState(1);

  const postsPerPage = 4;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const postData = await getPosts();
        setPosts(postData);
        setFilteredPosts(postData); // Initialize filtered posts with all posts
      } catch (error) {
        console.log("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  // Calculate the index of the last post on the current page
  const indexOfLastPost = currentPage * postsPerPage;
  // Calculate the index of the first post on the current page
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  // Get the current page of posts
  const currentPosts = filteredPosts.slice(indexOfFirstPost, indexOfLastPost); // Update to use filteredPosts

  // Change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  // Function to handle search
  const handleSearch = (query) => {
    setSearchQuery(query); // Update searchQuery state
    const filtered = posts.filter(
      (post) =>
        post.title.toLowerCase().includes(query.toLowerCase()) ||
        post.author.toLowerCase().includes(query.toLowerCase()) ||
        post.category.toLowerCase().includes(query.toLowerCase()) ||
        post.content.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredPosts(filtered); // Update filteredPosts state
    setCurrentPage(1); // Reset current page to 1 when search query changes
  };

  const handleFilter = (filter) => {
    setFilter(filter);
    const filtered = posts.filter((post) =>
      post.category.toLowerCase().includes(filter.toLowerCase())
    );
    setFilteredPosts(filtered);
    setCurrentPage(1); // Reset current page to 1 when filter changes
  };

  return (
    <div className="">
      <Banner />
      <Filters handleFilter={handleFilter} handleSearch={handleSearch} />
      <PostsCards posts={filteredPosts} />
      <Pagination
        currentPage={currentPage}
        postsPerPage={postsPerPage}
        totalPosts={posts.length}
        paginate={paginate}
      />
    </div>
  );
};

export default Posts;
