export const getFilteredPosts = async (filter) => {
  try {
    let url = "http://localhost:5050/api/posts";

    // Apply filtering and sorting based on the selected filter
    switch (filter) {
      case "newest":
        url += "?sortBy=newest";
        break;
      case "oldest":
        url += "?sortBy=oldest";
        break;
      case "A-Z":
        url += "?sortBy=title";
        break;
      case "NEWS":
        url += "?filterBy=news";
        break;
      case "Guides":
        url += "?filterBy=guides";
        break;
      default:
        break;
    }

    const response = await axios.get(url);
    return response.data;
  } catch (error) {
    console.error("Error getting filtered posts:", error);
    throw error;
  }
};
