import React from "react";

const SideBar = ({ post }) => {
  // Check if post and post.content exist before rendering
  if (!post || !post.content) {
    return null; // or handle the case where post or post.content is undefined
  }

  const handleClick = (id) => {
    const element = document.getElementById(id);
    if (element) {
      element.scrollIntoView({ behaviour: 'smooth', block: 'start'});
    }
  }

  return (
    <div
      className="
        lg:col-span-3 
        border-b 
        border-gray-400 
        xl:border-r 
        xl:border-gray-400 
        py-10
        "
    >
      {/* Table of Contents Goes Here */}
      <div className="text-xl pr-10">
        <h2 className="text-2xl text-center">Table of Contents</h2>
        <div className="my-6">
          <h2 className=""  onClick={(() => handleClick(post.content.intro.heading))}>
            1. {post.content.intro.heading}
          </h2>
        </div>
        {post.content.sections.map((section, index) => (
          <div key={index} className="my-6" onClick={(() => handleClick(section.heading))}>
            <h2 className="">
              {index + 2}. {section.heading}
            </h2>
          </div>
        ))}
        <div className="my-6">
          <h2 className="" >7. {post.content.conclusion.heading}</h2>
        </div>
      </div>
    </div>
  );
};

export default SideBar;

