import React, { useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import { getFeatureProduct } from "../services/productService";

const Navbar = () => {
  const [id, setId] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    const getData = async () => {
      try {
        // this should be changed to accomodate the products
        // gallery page by linking to /products --> products
        // page compenent which links to /products:id
        const productData = await getFeatureProduct();
        setId(productData._id);
      } catch (error) {
        console.log("Error fetching data:", error);
      }
    };

    getData();
  }, []);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const handleClickOutside = (event) => {
    if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
      setIsOpen(false);
    }
  };

  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleLinkClick = () => {
    setIsOpen(false);
  };

  return (
    <div className="pt-6 relative">
      {/* Hamburger icon */}
      <button
        onClick={toggleMenu}
        className="block md:hidden text-white focus:outline-none"
      >
        <svg
          className="h-8 w-8"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
        >
          {isOpen ? (
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M6 18L18 6M6 6l12 12"
            ></path>
          ) : (
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M4 6h16M4 12h16m-7 6h7"
            ></path>
          )}
        </svg>
      </button>

      {/* Dropdown menu */}
      {isOpen && (
        <div
          ref={dropdownRef}
          className="absolute top-12 right-4 bg-gray-900 bg-opacity-75 rounded-md shadow-md p-4"
        >
          <div className="flex flex-col items-start">
            <Link
              to="/"
              className="text-white py-3 px-6 px-4 block"
              onClick={handleLinkClick}
            >
              Home
            </Link>
            <Link
              to={`/products/${id}`}
              className="text-white py-2 px-4"
              onClick={handleLinkClick}
            >
              Products
            </Link>
            <Link
              to="/posts"
              className="text-white py-2 px-4 block"
              onClick={handleLinkClick}
            >
              Posts
            </Link>
            <Link
              to="/about"
              className="text-white py-2 px-4 block"
              onClick={handleLinkClick}
            >
              About
            </Link>
            <Link
              to="/contact"
              className="text-white py-2 px-4 block"
              onClick={handleLinkClick}
            >
              Contact
            </Link>
          </div>
        </div>
      )}
    </div>
  );
};

export default Navbar;
