import React from "react";

const SideBar = ({ post }) => {
  // Check if post and post.content exist before rendering
  if (!post || !post.content) {
    return null; // or handle the case where post or post.content is undefined
  }

  return (
    <div
      className="
        lg:col-span-3 
        border-b 
        border-gray-400 
        xl:border-r 
        xl:border-gray-400 
        px-10"
    >
      <div className="mx-6">
      <div className="my-6">
        <h2 className="text-xl">1. {post.content.intro.heading}</h2>
      </div>
      {post.content.sections.map((section, index) => (
        <div key={index} className="my-6">
          <h2 className="text-xl">
            {index + 2}. {section.heading}
          </h2>
        </div>
      ))}
      <div className="my-6">
        <h2 className="text-xl">7. {post.content.conclusion.heading}</h2>
      </div>
      </div>

    </div>
  );
};

export default SideBar;
