import React from "react";

const SideBar = ({ post }) => {
    // Check if post and post.content exist before rendering
    if (!post || !post.content) {
      return null; // or handle the case where post or post.content is undefined
    }
  return (
    <div className="lg:col-span-3 border-b border-gray-400 xl:border-r xl:border-gray-400 mr-16">
       {post.content.sections.map((section, index) => (
        <div key={index} className="my-6">
          <h2 className="text-xl">{index + 1}. {section.heading}</h2>
        </div>
      ))}
    </div>
  );
};

export default SideBar;
