import React from 'react'

const Pagination = ({posts}) => {
    const [currentPage, setCurrentPage] = useState(1);
    const postsPerPage = 12;
    const pageNumbers = Math.ceil(posts.length / postsPerPage);

    // Calculate the index of the last post on the current page
    const indexOfLastPost = currentPage * postsPerPage;
    // Calculate the index of the first post on the current page
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    // Get the current page of posts
    const currentPosts = posts.slice(indexOfFirstPost, indexOfLastPost);

    // Change page
    const paginate = (pageNumber) => setCurrentPage(pageNumber);
  return (
    <div>
      {/*  Pageination */}
      <div className="flex justify-between p-10">
        <button
          onClick={() => paginate(currentPage - 1)}
          disabled={currentPage === 1}
          className="px-4 py-2 bg-blue-500 text-white rounded-lg shadow-md mr-2"
        >
          Previous
        </button>
        {Array.from({ length: pageNumbers }, (_, i) => (
          <button
            key={i + 1}
            onClick={() => paginate(i + 1)}
            className={`px-4 py-2 ${
              currentPage === i + 1
                ? "bg-blue-500 text-white"
                : "text-blue-500"
            } rounded-lg shadow-md mr-2`}
          >
            {i + 1}
          </button>
        ))}
        <button
          onClick={() => paginate(currentPage + 1)}
          disabled={indexOfLastPost >= posts.length}
          className="px-4 py-2 bg-blue-500 text-white rounded-lg shadow-md"
        >
          Next
        </button>
      </div>
    </div>
  )
}

export default Pagination
