import React, { useState } from "react";
import { sendContactMessage } from "../../services/formService";

const Form = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    subject: "",
    message: "",
  });

  const sendEmail = event => {
    event.preventDefault();
    axios
     .post('/send', { ...state })
     .then(response => {
       setResult(response.data);
       setState({ name: '', email: '', subject: '', message: '' });
     })
     .catch(() => {
       setResult({ success: false, message: 'Something went wrong. Try again later'});
   });

  const onInputChange = (event) => {
    const { name, value } = event.target;

    setFormData({
      ...formData,
      [name]: value,
    });
  };

  return (
    <div className="py-20 px-10 md:px-20 lg:px-40 ">
      <div className="p-6 bg-blue-200 content-center rounded-lg hover:shadow-2xl">
        <div className="grid grid-cols-1 sm:grid-cols-1 md:grid-cols-2">
          <div>
            <h2 className="text-2xl pb-2">Reach Out to Us</h2>
            <p>
              As the creators of I-Meditate, we are passionate about leveraging
              technology to bring people together in pursuit of well-being and
              inner harmony.
            </p>
          </div>

          <div>
            <form onSubmit={sendEmail}>
                <label>Full Name</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  placeholder="Enter your full name"
                  onChange={onInputChange}
                />
                <label>Email</label>
                <input
                  type="text"
                  name="email"
                  value={formData.email}
                  placeholder="Enter your email"
                  onChange={onInputChange}
                />
                <label>Subject</label>
                <input
                  type="text"
                  name="subject"
                  value={formData.subject}
                  placeholder="Enter subject"
                  onChange={onInputChange}
                />
                <label>Message</label>
                <input
                  as="textarea"
                  name="message"
                  value={formData.message}
                  rows="3"
                  placeholder="Enter your message"
                  onChange={onInputChange}
                />
              <button className="bg-blue-500" type="submit">
                Submit
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}};

export default Form;
