import React from "react";

const FeatureBanner = ({featuredProduct}) => {

  return (
    <div className="bg-blue-200 py-8 px-4">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 p-4">
        <h2 className="text-2xl font-semibold mb-2 cols-2">{featuredProduct.name}</h2>
        <p className="mb-4">{featuredProduct.description}</p>
        <ul className="mb-4">
          {/* Check if features exist before mapping */}
          {featuredProduct.features &&
            featuredProduct.features.map((feature, index) => (
              <li key={index} className="mb-2">
                {feature}
              </li>
            ))}
        </ul>
        {/* Check if media exist before mapping */}
        {featuredProduct.media &&
          featuredProduct.media.map((file, index) => (
            <div key={index} className="mb-4">TEST MEDIA</div>
          ))}
        <p className="mb-4">Try it today!</p>
        <button className="bg-white text-blue-500 px-4 py-2 rounded-lg shadow-lg hover:bg-blue-500 hover:text-white transition duration-300">DOWNLOAD</button>
      </div>
    </div>
  );
};

export default FeatureBanner;
