import React, { useState, useEffect } from "react";
import { getSingleProduct } from "../services/productService";

import ProductBanner from "../components/ShowProduct/ProductBanner";
import Summary from "../components/ShowProduct/Summary";
import Features from "../components/ShowProduct/Features";
import Reasons from "../components/ShowProduct/Reasons";
import CallToAction from "../components/ShowProduct/CallToAction";

const ShowProduct = () => {
  const [product, setProduct] = useState({});

  const id = "66230e93862e1249483e6c43";

  useEffect(() => {
    const getData = async () => {
      try {
        const productData = await getSingleProduct(id);
        setProduct(productData);
      } catch (error) {
        console.log("Error fetching data:", error);
      }
    };

    getData();
  }, []);

  return (
    <div className="">
      <ProductBanner product={product} />

      <Summary product={product} />

      <Features product={product} />

      <Reasons product={product} />

      <CallToAction product={product} />

      {/* CALLTOACTION COMPONENT */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 p-4 pt-20">
        <div className="justify-center text-center">
          <div className="text-center justify-center">
            <h2 className="text-3xl font-semibold">
              So What Are You Waiting For?
            </h2>
            <p className="w-fit">
              Utilities for controlling how rows are positioned in multi-row
              positioned in multi-row flex and grid containers. Utilities for
              controlling how rows are positioned in multi-row flex and grid
              containers.
            </p>
            <button className="
              bg-blue-500
              text-white
              px-4 
              py-2 
              rounded-lg 
              shadow-lg 
              hover:bg-white
              hover:text-blue-500 
              transition 
              duration-300"
            >
              DOWNLOAD
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ShowProduct;
