import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { getFeatureProduct } from "../services/productService";

const Navbar = () => {
  const [id, setId] = useState("");
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    const getData = async () => {
      try {
        // this should be changed to accomodate the products
        // gallery page by linking to /products --> products
        // page compenent which links to /products:id
        const productData = await getFeatureProduct();
        setId(productData._id);
      } catch (error) {
        console.log("Error fetching data:", error);
      }
    };

    getData();
  }, []);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className='pt-6 relative'>
    {/* Hamburger icon */}
    <button onClick={toggleMenu} className='block text-white focus:outline-none'>
      <svg className='h-8 w-8' fill='none' viewBox='0 0 24 24' stroke='currentColor'>
        {isOpen ? (
          <path strokeLinecap='round' strokeLinejoin='round' strokeWidth='2' d='M6 18L18 6M6 6l12 12'></path>
        ) : (
          <path strokeLinecap='round' strokeLinejoin='round' strokeWidth='2' d='M4 6h16M4 12h16m-7 6h7'></path>
        )}
      </svg>
    </button>

    {/* Dropdown menu */}
    {isOpen && (
      <nav className='absolute top-0 right-20 w-full bg-gray-900 p-4'>
        <div className='flex flex-col items-start'>
          <Link to="/" className='text-white py-1'>Home</Link>
          <Link to="/products" className='text-white py-1'>Products</Link>
          <Link to="/posts" className='text-white py-1'>Posts</Link>
          <Link to="/about" className='text-white py-1'>About</Link>
          <Link to="/contact" className='text-white py-1'>Contact</Link>
        </div>
      </nav>
    )}
  </div>
