// export default Posts;
import React, { useState, useEffect } from "react";
import { getPosts } from "../services/postService";
import Banner from "../components/posts/Banner";
import Filters from "../components/posts/Filters";
import PostsCards from "../components/posts/PostsCards";
import Pagination from "../components/posts/Pagination";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [filteredPosts, setFilteredPosts] = useState([]);
  const [filter, setFilter] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [activeFilter, setActiveFilter] = useState(""); // State for active category filter

  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setPostsPerPage] = useState(4);

  // Calculate current posts to display based on pagination
  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = filteredPosts.slice(indexOfFirstPost, indexOfLastPost);

  useEffect(() => {
    const fetchData = async () => {
      try {
        // setLoading(true);
        const postData = await getPosts();
        setPosts(postData);
        setFilteredPosts(postData); // Initialize filtered posts with all posts
      } catch (error) {
        console.log("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const handleSearch = (query) => {
    setSearchQuery(query); // Update searchQuery state
    const filtered = applyFilters(posts, filter, query); // Apply both filters
    setFilteredPosts(filtered); // Update filteredPosts state
  };

  const handleFilter = (categoryFilter) => {
    setFilter(categoryFilter); // Update category filter state
    setActiveFilter(categoryFilter); // Update active category filter state
    const filtered = applyFilters(posts, categoryFilter, searchQuery); // Apply both filters
    setFilteredPosts(filtered); // Update filteredPosts state
    handlePagination(1);
  };

  const handlePagination = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Helper function to apply filters
  const applyFilters = (posts, categoryFilter, searchQuery) => {
    return posts.filter((post) => {
      const title = post.title.toLowerCase();
      const author = post.author.toLowerCase();
      const category = post.category.toLowerCase();
      const contentSections = post.content.sections
        .map((section) => `${section.heading} ${section.text}`)
        .join(" ")
        .toLowerCase();
      // Check if post passes category filter
      const passesCategoryFilter =
        categoryFilter === "" || category.includes(categoryFilter.toLowerCase());
      // Check if post passes search query
      const passesSearchQuery =
        searchQuery === "" ||
        title.includes(searchQuery.toLowerCase()) ||
        author.includes(searchQuery.toLowerCase()) ||
        category.includes(searchQuery.toLowerCase()) ||
        contentSections.includes(searchQuery.toLowerCase());
      return passesCategoryFilter && passesSearchQuery;
    });
  };

  return (
    <div className="">
      <Banner />
      <Filters handleFilter={handleFilter} handleSearch={handleSearch} activeFilter={activeFilter} />
      <PostsCards posts={currentPosts} />

      {/* PAGINATION */}
      <Pagination
        currentPage={currentPage}
        postsPerPage={postsPerPage}
        length={filteredPosts.length}
        handlePagination={handlePagination}
      />
    </div>
  );
};

export default Posts;
