import React, { useState, useEffect } from 'react';
import axios from 'axios';
// import { useParams } from 'react-router-dom';

const App = () => {
  const [posts, setPosts] = useState([]);
  const [featuredProduct, setFeaturedProduct] = useState({});

  const id = '66230e93862e1249483e6c43';
  const features = featuredProduct.features;

  useEffect(() => {
    //fetch product data from express server
    axios
      .get(`http://localhost:5050/api/products/${id}`)
      .then((response) => {
        setFeaturedProduct(response.data);
        console.log(featuredProduct);
    })
    .catch((error) => {
      console.log('Error getting product data from server')
    });

    //fetch post data from express server
    axios
      .get(`http://localhost:5050/api/posts`)
      .then((response) => {
        setPosts(response.data);
        console.log(posts);
    })
    .catch((error) => {
      console.log('Error getting post data from server.')
    }
  )}, []);

  return (
    <div>
      <h1>Product Showcase Website</h1>
      <h2>{featuredProduct.name}</h2>
      <p>{featuredProduct.description}</p>
      <ul>
        {features.map((feature, index) => {
          return <li key={index}>{feature}</li>
        })}
      </ul>

      <hr />

      <h2>Latest Posts</h2>
        {posts.map(post => (
          <>
          <h4 key={post._id}>{post.title}</h4>
          <p>{post.content}</p>
          </>
        ))}
    </div>
  )
}

export default App;
