import React, { useState, useEffect } from "react";
import { getPosts } from "../services/postService";
import Banner from "../components/posts/Banner";
import Filters from "../components/posts/Filters";
import PostsCards from "../components/posts/PostsCards";

const Posts = () => {
  const [posts, setPosts] = useState([]);
  const [filter, setFilter] = useState(null);

  useEffect(() => {
    const getData = async () => {
      try {
        const postData = await getPosts();
        setPosts(postData);
      } catch (error) {
        console.log("Error fetching data:", error);
      }
    };

    getData();
  }, []);

  return (
    <div className="">
      <Banner />

      <Filters />

      <PostsCards />
      {/* POSTCARDS COMPONENT */}
      <div className="py-12 px-20">
        <div className=" grid grid-cols-1 sm:grid-cols-2 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 content-center text-center">
          {posts.map((post) => (
            <div className="bg-blue-200 rounded-lg shadow-md overflow-hidden">
              <img
                src={post.featureImage}
                alt="[FEATURE IMG]"
                className="w-full h-auto p-10"
              />
              <div className=" bg-blue-500 p-4 opacity-[80%]">
                <h2 className="text-xl text-white font-semibold mb-2 ">{post.title}</h2>
                {/* Other post details */}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Posts;
