import React, { useState } from 'react'

const Pagination = ({ currentPage, postsPerPage, totalPosts, paginate }) => {
    const pageNumbers = Math.ceil(totalPosts / postsPerPage);

    return (
      <div className="flex justify-center px-20 pb-10">
        <button
          onClick={() => paginate(currentPage - 1)}
          disabled={currentPage === 1}
          className="px-4 py-2 bg-blue-500 text-white rounded-lg shadow-md mx-40"
        >
          Previous
        </button>
        {Array.from({ length: pageNumbers }, (_, i) => (
          <button
            key={i + 1}
            onClick={() => paginate(i + 1)}
            className={`px-4 py-2 ${currentPage === i + 1 ? 'bg-blue-500 text-white' : 'bg-gray-200 text-gray-800'} rounded-lg shadow-md mr-2`}
          >
            {i + 1}
          </button>
        ))}
        <button
          onClick={() => paginate(currentPage + 1)}
          disabled={currentPage === pageNumbers}
          className="px-4 py-2 bg-blue-500 text-white rounded-lg shadow-md mx-40"
        >
          Next
        </button>
      </div>
    );
  }


export default Pagination
